import React from 'react';
import coverImage from '../../assets/cover/cover-image.jpg';
function About() {
  return (
    <section className="my-5">
      <h1 id="about">Who am I?</h1>
      <img src={coverImage} className="my-pic" style={{ width: "30%" }} alt="cover" />
      <div className="my-2">
        <p>
          Welcome! My name is Ezra Katz. I am a full stack developer from Miami, Florida. I have a degree in Liberal Arts from New College of Florida and worked in education, teaching kindergarten for years before I fell in love with programming. Please browse my portfolio and get to know my work. Don't hesitate to reach out to me if you think we would be a good match to work on a project together!
        </p>
        <div><iframe src="https://giphy.com/embed/xThtagT1QVZLTzEBO0" width="100%" height="100%" frameBorder="0" class="giphy-embed" allowFullScreen></iframe></div><p><a href="https://giphy.com/gifs/animation-art-design-xThtagT1QVZLTzEBO0">via GIPHY</a></p>
      </div>
    </section>
  );
}

export default About;
